$.AppFilter.getFiterJson = function () {
    var that = this;
    var searchParams = {};
    for (var key in that.$Controlls) {
        var item = that.$Controlls[key];
        var v = item.GetValue();
        if ($.isArray(v)) {
            searchParams[key] = v;
        } else if (typeof v == "string") {
            if (v && v.length > 0) {
                v = v.split(';');
                var newV = [];
                for (var i = 0; i < v.length; i++) {
                    if (v[i] == undefined || v[i] == 'undefined') {
                        newV.push('');
                    } else if (v[i] != '') {
                        newV.push(v[i]);
                    }
                }
                searchParams[key] = newV;
            }
        } else {
            searchParams[key] = [];
        }
    }
    return searchParams;
};
