            //
            var formatDate = function (date) {
                var year = date.getFullYear();
                var month = date.getMonth() + 1;
                var day = date.getDate();
                return year + '-' + (month < 10 ? ('0' + month) : month) + '-' + (day < 10 ? ('0' + day) : day);
            };
            //
            var thisDay = function () {
                var date = new Date();
                date = formatDate(date);
                return date + ';' + date;
            };
            //
            var thisWeek = function () {
                var date = new Date();
                var year = date.getFullYear();
                var month = date.getMonth();
                var day = date.getDate();
                var dayOfWeek = date.getDay();
                var start = new Date(year, month, day - dayOfWeek);
                start = formatDate(start);
                var end = new Date(year, month, day + (6 - dayOfWeek));
                end = formatDate(end);
                return start + ';' + end;
            };
            //
            var thisMonth = function () {
                var date = new Date();
                var year = date.getFullYear();
                var month = date.getMonth();
                var start = new Date(year, month, 1);
                start = formatDate(start);
                var end = new Date(year, month + 1, 0);
                end = formatDate(end);
                return start + ';' + end;
            };
            //
            var thisQuarter = function () {
                var startMonth = 0;
                var date = new Date();
                var year = date.getFullYear();
                var month = date.getMonth() + 1;
                if (month < 3) {
                    startMonth = 0;
                } else if (month > 2 && month < 6) {
                    startMonth = 3;
                } else if (month > 5 && month < 9) {
                    startMonth = 6;
                } else if (month > 8) {
                    startMonth = 9;
                }
                var start = new Date(year, startMonth, 1);
                var end = new Date(year, startMonth + 3, 0);
                start = formatDate(start);
                end = formatDate(end);
                return start + ';' + end;
            };
            //
            var thisYear = function () {
                var date = new Date();
                var year = date.getFullYear();
                var start = new Date(year, 0, 1);
                var end = new Date(year, 12, 0);
                start = formatDate(start);
                end = formatDate(end);
                return start + ';' + end;
            };
